function catopo(v)%Function CATOPO(V) -- Topographic map of Central America --%  Draws a map with gray-filled topographic contours over the%  domain V = [265,285,5,20] (default) or any user-specified %  subdomain thereof. Spatial resolution is 5 minutes of arc.%  Contours are drawn at elevations of 0, 300, 700, 1100, 1500, %  1900, and 3000 meters. MatlabPath must include the location%  of the topographic data file CATOPO5.MAT. Works with Matlab %  versions 4 or 5.%%  D.Enfield and J.Harris, NOAA/AOML (Miami, 5-19-97)% Initialize & load dataif nargin == 0	v = [255,285,5,20]; else 	if(v(1)<0),v(1)=v(1)+360,end;	if(v(2)<0),v(2)=v(2)+360,end;	v = fix([max(265,v(1)),min(285,v(2)),max(5,v(3)),min(20,v(4))]);endcv = [300, 700, 1100, 1500, 1900, 3000]; load catopo5.mat; ver = version; ver = str2num(ver(1)) - 3;col = ['w';'k'];% prepare the basic mapjx = find(xx>=v(1) & xx<=v(2)); jy = find(yy>=v(3) & yy<=v(4)); pcolor(xx(jx),yy(jy),map(jy,jx)); hold onshading('flat')colormap(flipud(gray(64)))caxis([-1000,5300]); hc = find(isnan(map) == 1); map(hc) = -100*ones(size(hc));[cl,hc] = contour(xx+1/24,yy+1/24,map,cv,col(ver)); set(hc,'linewidth',0.1)[cl,hc] = contour(xx+1/24,yy+1/24,map,[0,0],col(ver)); set(hc,'linewidth',1)set(gca,'fontname','palatino','fontweight','demi','fontsize',18)% Set axis labels and plot grid lines at 5-degree incrementsx = v(1):v(2); y = v(3):v(4); jx = find(x == 5*floor(x/5)); jy = find(y == 5*floor(y/5));if ver == 1	xlbl = setstr(ones(length(x),1)*['   ']);	ylbl = setstr(ones(length(y),1)*['   ']);	for k=1:length(jx)  		str=str2mat([(int2str(360-x(jx(k)))) setstr(87*ones(length(jx(k)),1)')]);  		xlbl(jx(k),1:length(str))=str;	end	for k=1:length(jy)  		str=str2mat([(int2str(y(jy(k)))) setstr(78*ones(length(jy(k)),1)')]);  		ylbl(jy(k),1:length(str))=str;	end	set(gca,'AspectRatio',[(v(2)-v(1))/(v(4)-v(3)),1])	set(gca,'xticklabels',xlbl,'yticklabels',ylbl)else	xlbl = char(ones(length(x),1)*['   ']);	ylbl = char(ones(length(y),1)*['   ']);	xlbl(jx,:) = [int2str(360-x(jx)'),char(87*ones(length(jx),1))];	ylbl(jy,:) = [int2str(y(jy)'),char(78*ones(length(jy),1))];	set(gca,'DataAspectRatio',[1,1,1]) 	set(gca,'xticklabel',xlbl,'yticklabel',ylbl)endset(gca,'xlim',v(1:2),'ylim',v(3:4))set(gca,'xtick',x,'ytick',y) plot([x(jx);x(jx)],[v(3:4)'*ones(1,length(jx))],['--',col(ver)])plot([v(1:2)'*ones(1,length(jy))],[y(jy);y(jy)],['--',col(ver)])